import {
  createColumnHelper,
  getCoreRowModel,
  useReactTable,
} from '@tanstack/react-table';
import { useState } from 'react';
import { StatusChip } from 'shared/components/StatusChip';
import { ProductTableAction } from '../components/ProductTableAction';
import { ProductStatus } from '../enum';

interface IProducts {
  productImg: string;
  productName: string;
  price: number;
  stock: number;
  status: ProductStatus;
  action?: string;
  id?: string;
}
const products: IProducts[] = [
  {
    productImg:
      'data:image/jpeg;base64,',
    productName: 'Product 1',
    price: 10.99,
    stock: 50,
    id: '123',

    status: ProductStatus.DRAFT,
  },
  {
    productImg:
      'https://i.pinimg.com/564x/33/9d/0e/339d0e68e577beefbf9ee8ae615b83cd.jpg',
    productName: 'Product 2',
    price: 19.99,
    stock: 20,
    id: '456',

    status: ProductStatus.PUBLISHED,
  },
  // Add more products as needed
  {
    productImg:
      'data:image/jpeg;base64,',
    productName: 'Product 3',
    price: 5.99,
    stock: 0,
    id: '789',
    status: ProductStatus.DRAFT,
  },
  {
    productImg:
      'data:image/jpeg;base64,',
    productName: 'Product 4',
    price: 15.99,
    stock: 10,
    id: '10,11,12',

    status: ProductStatus.PUBLISHED,
  },
  // Add more products as needed
];

const columnHelper = createColumnHelper<IProducts>();
export const useProductTable = () => {
  const [data, setData] = useState<IProducts[]>(products);
  const columns = [
    columnHelper.accessor((row) => row.productImg, {
      cell: (info) => (
        <img
          src={info.getValue()}
          alt={info.getValue()}
          className="w-[50px] h-[50px]"
        />
      ),
      header: 'Product',
    }),
    columnHelper.accessor((row) => row.productName, {
      cell: (info) => info.getValue(),
      header: 'Name',
    }),
    columnHelper.accessor((row) => row.price, {
      cell: (info) => info.getValue(),
      header: 'Price',
    }),
    columnHelper.accessor((row) => row.stock, {
      cell: (info) => info.getValue(),
      header: 'Quantity',
    }),
    columnHelper.accessor((row) => row.status, {
      cell: (info) => (
        <div className="w-[50%]">
          <StatusChip status={info.getValue()} />
        </div>
      ),
      header: 'Status',
    }),
    columnHelper.accessor('action', {
      cell: ProductTableAction,
      header: 'Action',
      meta: {
        deleteData: (id: string) => {
          setData((prevData) =>
            prevData.filter((product) => product.id !== id)
          );
        },
      },
    }),
  ];

  const table = useReactTable({
    data,
    columns,
    getCoreRowModel: getCoreRowModel(),
  });
  return {
    ...table,
  };
};
